/* overlay modal */
.ReactModal__Body--open {
    overflow: hidden;
}

.modal {
    background-color: #fff;
    display: block;
    padding: 1rem 1rem 1rem 1rem;
    border-radius: 4px;
    position: relative;
    flex-grow: 0;
    overflow: auto;
    max-height: 100%;
    margin-bottom: 0;
    z-index: 1010;
}

.modal:focus,
.modal--focus {
    box-shadow: 0 0 0px 3px #254b6d;
    outline: none;
}

.modal__overlay {
    position: fixed;
    z-index: 1000;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    background-color: rgba(61, 56, 49, 0.7);
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 1rem;
}

/* lukk-knapp */

.navno-dekorator .lukknapp {
    font: 0/0 a;
    color: transparent;
    text-shadow: none;
    border: 0;
    position: relative;
    width: 2rem;
    height: 2rem;
    cursor: pointer;
    color: #78706a;
    border: 2px solid currentColor;
    background-color: transparent;
    border-radius: 2px;
}

.navno-dekorator .lukknapp:before,
.navno-dekorator .lukknapp:after {
    content: '';
    position: absolute;
    width: 1.25rem;
    height: 3px;
    top: calc(50% - 0.175rem);
    left: 50%;
    background-color: currentColor;
}

.navno-dekorator .lukknapp:before {
    -webkit-transform: translate(-50%, 50%) rotate(-45deg);
    transform: translate(-50%, 50%) rotate(-45deg);
}

.navno-dekorator .lukknapp:after {
    -webkit-transform: translate(-50%, 50%) rotate(45deg);
    transform: translate(-50%, 50%) rotate(45deg);
}

.navno-dekorator .lukknapp:hover {
    background-color: currentColor;
}

.navno-dekorator .lukknapp:hover:before,
.navno-dekorator .lukknapp:hover:after {
    background-color: #fff;
}

.navno-dekorator .lukknapp:active {
    color: #19548a;
}

.navno-dekorator .lukknapp:focus {
    outline: none;
    box-shadow: 0 0 0 3px #254b6d;
}

.navno-dekorator .lukknapp--bla {
    color: #0067c5;
}

.navno-dekorator .lukknapp--hvit {
    color: #fff;
}

.navno-dekorator .lukknapp--hvit:hover {
    background-color: #66cbec;
    border-color: #66cbec;
    color: #78706a;
}

.navno-dekorator .lukknapp--hvit:hover:before,
.navno-dekorator .lukknapp--hvit:hover:after {
    background-color: #3e3832;
}

.navno-dekorator .lukknapp--overstHjorne {
    position: absolute;
    top: 1rem;
    right: 1rem;
}

.modal__overlay:active .modal:not(:active) .modal__lukkknapp--shake {
    -webkit-transform: rotate(10deg);
    transform: rotate(10deg);
    transition: -webkit-transform 100ms cubic-bezier(0.82, -2, 1, 0.3);
    transition: transform 100ms cubic-bezier(0.82, -2, 1, 0.3);
    transition: transform 100ms cubic-bezier(0.82, -2, 1, 0.3), -webkit-transform 100ms cubic-bezier(0.82, -2, 1, 0.3);
}

.modal__lukkknapp--shake {
    -webkit-transform: rotate(0deg);
    transform: rotate(0deg);
    transition: -webkit-transform 100ms cubic-bezier(0.82, 2, 1, 0.3);
    transition: transform 100ms cubic-bezier(0.82, 2, 1, 0.3);
    transition: transform 100ms cubic-bezier(0.82, 2, 1, 0.3), -webkit-transform 100ms cubic-bezier(0.82, 2, 1, 0.3);
}

